
import React, { useState } from 'react';
import './ThemeColor.css';
import ParticlesComponent from '../particles'; // Ensure this path is correct

const ThemeChange = () => {
  const [checked, setChecked] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('green');

  const handleThemeChange = () => {
    setChecked(!checked);
    const newColor = checked ? 'green' : '#182E4C'; // Toggle between colors
    setBackgroundColor(newColor);
  };

  return (
    <div className='sizee'>
      
      
          <label class="switch">
          <input type="checkbox" onChange={handleThemeChange}/>
          <span class="slider"></span>
          <img
            class="off"
            src="data:image/jpeg;base64,"
          />
          <img
            class="on"
            src="data:image/jpeg;base64,"
          />
        </label>
      
      <ParticlesComponent color={backgroundColor} id="tspp" />
    </div>
  );
};

export default ThemeChange;
